import { useEffect, useState } from "react";

const ProductPage = (props) => {
const [productData, setProductData] = useState([]);
useEffect(() => {
    const fetchProducts = async () => {
    const response = await fetch("https://fakestoreapi.com/products");
    const dataObject = await response.json();
    setProductData(dataObject);
    console.log(dataObject);
    };
    fetchProducts();
}, []);

return (
    <ul className="productPageContainer wrapper">
    {productData.map((product) => {
        return (
          <li className="productDetail" key={product.id}>
            <div className="imageContainer">
              <img className="image" src={product.image} alt={product.title} />
            </div>
            <p>{product.title}</p>
            <p>
              {`$`}
              {product.price}
            </p>
            <div className="buttonContainer">
              <button onClick={props.decrease}>
                <i className="fa fa-light fa-circle-minus"></i>
              </button>
              <span className="counter">{props.cartItem}</span>
              <button onClick={props.increase}>
                <i className="fa fa-light fa-circle-plus"></i>
              </button>
            </div>
          </li>
        );
    })}
    </ul>
);
};
export default ProductPage;
