import { useState } from "react";
const SingleProduct =({productDetail, cart})=>{
    const [setCartItem]=useState(0)
    const increase = ()=>{
        setCartItem( cart[productDetail.id] += 1) ;
    }
    const decrease=()=>{
        if (cart[productDetail.id] > 0) {
          setCartItem((cart[productDetail.id] -= 1));
        }
    }

    return(
        <>
        <div>
            <div className="imageContainer">
                <img className="image"src={productDetail.image} />
            </div>
            <p>{productDetail.title}</p>
            <p>{`$`}{productDetail.price}</p>
            <div>
                <button onClick={decrease}><i class="fa fa-light fa-circle-minus"></i></button>
                <span className="counter">{cart[productDetail.id].length}</span>            
                <button onClick={increase}><i class="fa fa-light fa-circle-plus"></i></button>         
            </div>   
        </div>    

        </>
    );
}

export default SingleProduct;
