import { useEffect, useState } from "react";

const ProductPage = ()=>{
const [productData, setProductData] = useState([]);
const [cartItem, setCartItem]=useState(0)
    const increase = ()=>{
        setCartItem( cartItem + 1) ;
    }
    const decrease=()=>{
        if (cartItem > 0){
            setCartItem(cartItem - 1);
        }
    }
useEffect(() => {
    const fetchProducts = async () => {
    const response = await fetch("https://fakestoreapi.com/products");
    const dataObject = await response.json();
    setProductData(dataObject);
    console.log(dataObject)
    };
    fetchProducts();
}, []);

    return(
        <ul className="productPageContainer wrapper">
            {
                (productData.map((product)=>{
                    return(
                    <li className="productDetail" key={product.id}>
            <div className="imageContainer">
                <img className="image"src={product.image} />
            </div>
            <p>{product.title}</p>
            <p>{`$`}{dataObject.price}</p>
            <button onClick={decrease}><i class="fa fa-light fa-circle-minus"></i></button>
            <span className="counter">{cartItem}</span>            
            <button onClick={increase}><i class="fa fa-light fa-circle-plus"></i></button>            

            </li>
                    )
                })) 
        }



        </ul>
    )

}
export default ProductPage;
