const SingleProduct =({productDetail})=>{
    const [cartItem, setCartItem]=useState("0")
    const [addProduct, setAddProduct]= useState(false)
    const [removeProduct, setRemoveProduct]= useState(true)




    return(
        <>
        <div className="productDetail">
            <div className="imageContainer">
                <img className="image"src={productDetail.image} />
            </div>
            <p>{productDetail.title}</p>
            <p>{`$`}{productDetail.price}</p>
            <button onClick={()=>setAddProduct(!addProduct)}class="addButton button">
            Add product to cart</button>
            {
                addProduct? 
                <button onClick={()=>setRemoveProduct(!removeProduct)}class="removeButton button">Remove product</button>
                : null
            }
            {/* {
                removeProduct?
                    <button onClick={()=>setAddProduct(!addProduct)}class="addButton button">
                    Add product to cart</button>
                    : null
            } */}

        </div>
        </>
    );
}

export default SingleProduct;
